entry.value:
var exists = false;
$.ajax({
    url: "designer?action=checkApp&name=" + $("#rapid_P9_C8_").val(),
    type: "GET",    
    async: false,                
    success: function(result) {
        if (result) {
            exists = true;
        }
    }
});
if (exists) {
  $("#rapid_P9_C46_").html("An application with this name already exists.<br/>Continue with the import only if you wish to overwrite it");
} else {
  $("#rapid_P9_C46_").html("");
}

