validation.javaScript:
var file = $("#file").val();
if (!file) return "A file must be chosen using the button above";
if (file.toLowerCase().indexOf(".zip") != file.length - 4) return "File must be a .zip";
var name = $("#rapid_P9_C8_").val();
if (!name) return "An application name must be provided";
if (!name.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application name can only contain characters a-z, A-Z, 0-9, ., -, and _";
var version = $("#rapid_P9_C57_").val();
if (!version) return "An application version must be provided";
if (!version.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application version can only contain characters a-z, A-Z, 0-9, ., -, and _";
var exists = false;
$.ajax({
    url: "designer?action=checkVersion&name=" + name + "&version=" + version,
    type: "GET",    
    async: false,                
    success: function(result) {
        if (result) {
            exists = true;
        }
    }
});
if (exists) return "An application with this version already exists";

entry.value:
var f = window.frames["importIFrame"];
if (!f) f = document.getElementById("importIFrame").contentDocument;
var r = $(f.document.body).text();
if ((r) && (r != "")) {
  if (r.indexOf("Error") == 0) {
    alert(r);		
  } else {    		
    location.reload();
  }
}
