control.getHtmlFunction:

// return our html for adding to our parent object	
var value = "";
if (this.value) value = this.value  
switch (this.controlType) {
	case "N" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='text'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "L" : return "<textarea id='" + this.id + "' name='" + this.id + "' class='input" + getStyleClasses(this) + (this.autoHeight ? " autoheight" : "")  + "' " + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + (this.placeholder ? " placeholder='" + escapeApos(this.placeholder) + "'" : "") + ">" + value + "</textarea>";
	case "Num" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='number'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "Tel" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='tel'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "Eml" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='email'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "P" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='password'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "'></input>";
	case "R" : return "<input id='" + this.id + "' name='" + this.id + "' value='" + escapeApos(value) + "' type='text'" + (this.maxLength ? " maxlength='" + this.maxLength + "'" : "") + " " + (this.placeholder ? " placeholder='" + this.placeholder + "'" : "") + " class='input" + getStyleClasses(this) + "' readonly='readonly'></input>";
}

control.setDataJavaScript:

var control = $("#" + id);
var value = "";
if (data != null && data !== undefined) {	
	data = makeDataObject(data, field);
	if (data.rows && data.rows[0]) {	        		
		if (field && data.fields && data.fields.length > 0) {
			for (var i in data.fields) {
				if (data.fields[i] && data.fields[i].toLowerCase() == field.toLowerCase()) {
					value = data.rows[0][i];
					break;
				}
			}
		} else {
			if (data.rows[0][0] != null && data.rows[0][0] !== undefined) {
				value = data.rows[0][0];
			} 			
		}
	} 
} 
control.val(value);
if (changeEvents) control.trigger("change");
